import requests, json, functools
from constants import ETHERSCAN_API_URL_MAP, DUMMY_HEADER
from secrets import ETHERSCAN_API_KEY


class WrappedContract:
    """Class exposing Contract functions."""

    def __init__(self, address, chain, network, web3instance):
        self.Web3Instance = web3instance
        self.contract_address = web3instance.toChecksumAddress(address)
        self.etherscan_api = ETHERSCAN_API_URL_MAP[network]
        self.chainId = chain

    def get_abi_from_etherscan(self) -> str:
        """Request ABI from etherscan for given published contract"""

        response = requests.get(
            self.etherscan_api,
            params={
                "module": "contract",
                "action": "getabi",
                "address": self.contract_address,
                # "tag": "latest",
                "apikey": ETHERSCAN_API_KEY,
            },
            headers={'User-Agent': DUMMY_HEADER}
        )
